Get-VirtualHardDisk -VMMServer SRVVMM01 -All | Select-Object @{Label="VM Host";Expression={$_.VMHost}},@{Label="Name";Expression={$_.Name}},@{Label="Space To Grow";Expression={[Math]::Truncate(($_.MaximumSize-$_.Size) / 1GB)}},@{Label="VHDType";Expression={$_.VHDType}},@{Label="MaxSize";Expression={[Math]::Truncate(($_.MaximumSize) / 1GB)}} | Where-Object {($_.VHDType -Match "DynamicallyExpanding" -And $_.Name -NotMatch "{")} | Sort-Object "Space To Grow" -Descending |  Export-Csv C:\Users\admin\1.csv